import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Input,
  Output,
  QueryList,
  ViewChild,
  ViewChildren
} from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AplicationService } from 'src/app/service/aplication.service';

@Component({
  selector: 'create-app-popap',
  templateUrl: './create-app-popap.component.html',
  styleUrls: ['./create-app-popap.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class CreateAppPopapComponent {
  @Input() isActive: boolean = false;
  @Output() closePopapEmitter: EventEmitter<any> = new EventEmitter();
  authFormGroup = new FormGroup({
    name: new FormControl('', [Validators.required, Validators.minLength(6)]),
    description: new FormControl('', [
      Validators.required,
      Validators.minLength(5),
      Validators.maxLength(105)
    ])
  });

  get name() {
    return this.authFormGroup.get('name');
  }

  get description() {
    return this.authFormGroup.get('description');
  }

  constructor(private router: Router, private appService: AplicationService) {}

  closePopap() {
    this.closePopapEmitter.emit();
  }

  saveAplication() {}
}
